jQuery(document).ready(function(){INSURANCE_ANCORA_STORAGE.media_frame=null;INSURANCE_ANCORA_STORAGE.media_link="";jQuery(".insurance_ancora_media_selector").on("click",function(a){insurance_ancora_show_media_manager(this);a.preventDefault();return false})});function insurance_ancora_show_media_manager(a){INSURANCE_ANCORA_STORAGE.media_link=jQuery(a);if(INSURANCE_ANCORA_STORAGE.media_frame){INSURANCE_ANCORA_STORAGE.media_frame.open();return false}INSURANCE_ANCORA_STORAGE.media_frame=wp.media({title:INSURANCE_ANCORA_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:INSURANCE_ANCORA_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:INSURANCE_ANCORA_STORAGE.media_link.data("update"),close:true}});INSURANCE_ANCORA_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+INSURANCE_ANCORA_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(INSURANCE_ANCORA_STORAGE.media_link.data("multiple")===true){INSURANCE_ANCORA_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=INSURANCE_ANCORA_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});INSURANCE_ANCORA_STORAGE.media_frame.open();return false};